import React, {useContext} from 'react';
import {PlanetsContext} from '../../context/PlanetsContext.js';

const NavBar = (props) => {

	const {navBarClass, menuClass, navBarToggle} = props

	const {planetsTab, dispatch} = useContext(PlanetsContext);

	const handleClick = (planet) => {
		console.log({planet})
	}

	const navBar = (

		<div className={'navBar ' + navBarClass}>		

		<div className='socialsNav' onClick={(socials) => handleClick()}>SOCIALS</div>
		<div className='newsNav' onClick={(news) => handleClick()}>NEWS</div>
		<div className='aboutNav' onClick={(about) => handleClick()}>ABOUT</div>
		<div className='artNav' onClick={(art) => handleClick()}>ART</div>
		<div className='musicNav' onClick={(music) => handleClick()}>MUSIC</div>
		<div className='contactNav' onClick={(contact) => handleClick()}>CONTACT PRO</div>

		</div>

	)

	const menu = (

		<div className='menu' onClick={navBarToggle}>
		<div className={'line1 ' + menuClass}/>
		<div className={'line2 ' + menuClass}/>
		<div className={'line3 ' + menuClass}/>
		</div>

	)

	return (
		<div>
		{navBar}
		{menu}
		</div>
	);
};
export default NavBar;
