import React, {createContext, useReducer} from 'react';

export const PlanetsContext = createContext();

	const initialState = [
                                {id:'news', planetToggle:false, infoToggle:false, classes:'planet planet1'},
                                {id:'about', planetToggle:false, infoToggle:false, classes:'planet planet2'},
                                {id:'art', planetToggle:false, infoToggle:false, classes:'planet planet3'},
                                {id:'socials', planetToggle:false, infoToggle:false, classes:'planet planet4'},
                                {id:'music', planetToggle:false, infoToggle:false, classes:'planet planet5'},
                                {id:'contact', planetToggle:false, infoToggle:false, classes:'planet planet6'}
        ];

export const planetReducer = (planetsTab, action) => {
		switch(action.type){
			case 'switchPlanetToggle':
			    	planetsTab.forEach(planet => planet.planetToggle=false)
				return [
        			...planetsTab.slice(0, action.index),
        			{
          			...planetsTab[action.index],
          			planetToggle: !planetsTab[action.index].planetToggle
        			},
        			...planetsTab.slice(action.index + 1)
      				];
			case 'switchInfoToggle':
			    	planetsTab.forEach(planet => planet.planetToggle=false)
				planetsTab.filter((planet,index) => index !== action.index).forEach(planet => {
					if(planet.classes.indexOf(' none') === -1){planet.classes = planet.classes + ' none'}})
				console.log(planetsTab)
				return [
                                ...planetsTab.slice(0, action.index),
                                {
                         	...planetsTab[action.index],
                                infoToggle: !planetsTab[action.index].infoToggle 
                                },
                                ...planetsTab.slice(action.index + 1)
                                ];
			case 'reset':
				return intialState;
            		default:
                		return planetsTab;
		}
}
	const PlanetsContextProvider = (props) => {

	const [planetsTab, dispatch] = useReducer(planetReducer, initialState);
		console.log(planetsTab)
	return (
		<PlanetsContext.Provider value={{planetsTab, dispatch}}>
		{props.children}
		</PlanetsContext.Provider>
	);
};
export default PlanetsContextProvider
