		function handleCardNumber() {
				if(cardNumber === 0){dataLocal.content}
				if(cardNumber === 1) {'cards appear here'}
		}

		const cards = (
				handleCardNumber()
		)

		return (
				<div className='cards'>
				{cards}
				</div>
		)
}

export default memo(ArtCards);
