import React, {useContext, useRef} from 'react';
import PlanetImg from '../../assets/pics/planet.jpg';
import {PlanetsContext, planetReducer} from '../../context/PlanetsContext.js'; 

const Planets = () => {

	const {planetsTab, dispatch} = useContext(PlanetsContext);

	const inputRef = useRef('')

	const planets = (

		planetsTab.map((planet, index) => (
			<div key={planet.number} className={'planet ' + planet.number} onClick={() => dispatch({type: 'switchPlanetToggle', index}), inputRef.current.value = index}>
				<img className='planetImg' src={PlanetImg} alt={planet.id}/>
				<div className='planetinfo'/>
			</div>
			)
		)
	);

	return (
		<div>
			{planets}		
		</div>
	)
};
export default Planets
