import React, {createContext, useState} from 'react';

export const PlanetsContext = createContext();

const PlanetsContextProvider = (props) => {

	const [planetsTab, setPlanetsTab] = useState([
                {number:'planet1', planetToggle:false},
                {number:'planet2', planetToggle:false},
                {number:'planet3', planetToggle:false},
                {number:'planet4', planetToggle:false},
                {number:'planet5', planetToggle:false},
                {number:'planet6', planetToggle:false}
        ]);

	return (
		<PlanetsContext.Provider value={{planetsTab}}>
		{props.children}
		</PlanetsContext.Provider>
	);
};
export default PlanetsContextProvider
