import React, {createContext, useReducer} from 'react';

export const PlanetsContext = createContext();

export const planetReducer = (planetsTab, action) => {
		switch(action.type){
			case 'switchPlanetToggle':
				for(let i=0; i<planetsTab.length; i++){
                      			if(i === action.index) 
                      			{planetsTab[i].planetToggle = true}
                      			else
					{planetsTab[i].planetToggle = false}
				};
			return planetsTab;
				}
	}

	const PlanetsContextProvider = (props) => {

	const [planetsTab, dispatch] = useReducer(planetReducer, [
                {number:'planet1', planetToggle:false, infoToggle:false},
                {number:'planet2', planetToggle:false, infoToggle:false},
                {number:'planet3', planetToggle:true, infoToggle:false},
                {number:'planet4', planetToggle:false, infoToggle:false},
                {number:'planet5', planetToggle:false, infoToggle:false},
                {number:'planet6', planetToggle:false, infoToggle:false}
        ]);

	return (
		<PlanetsContext.Provider value={{planetsTab, dispatch}}>
		{props.children}
		</PlanetsContext.Provider>
	);
};
export default PlanetsContextProvider
