import React from 'react';
import IntroGalaxy from '../../assets/pics/introGalaxy.jpg';

export default function Intro(props){

	const {introScreen, introTimeout} = props

	const title = (
		<div className='title'>
		<div className='titleTara'>TARAYAH</div>WORLD
		</div>
	)

	const galaxy = (

		<div>
		<img src={IntroGalaxy} alt='the universe is spining' className='galaxy'/>
		<div className='galaxyShadow'/>

		<img src={IntroGalaxy} alt='the universe is spining' className='galaxy galaxyBis'/>
		<div className='galaxyShadow galaxyBis'/>

		<img src={IntroGalaxy} alt='the universe is spining' className='galaxy galaxyTer'/>
		<div className='galaxyShadow galaxyTer'/>

		</div>

	);

	const enter = (

		<div>
		<button className='introButton' onClick={introTimeout}>
		<div className='introButtonEnter'>ENTER</div>
		prepare to explore

		</button>
		</div>

	);

	return(
	<div className={introScreen}>

		{title}
		{galaxy}
		{enter}

	</div>
	);
};
