	const screenDivClasses = (classNames(
		'screen',
		{flex:planet.infoToggle},
		{opacityOff:planet.infoToggle}
	))

	const screen = (
		<div>
		{planet.id}
		</div>
	)

		console.log(firebase.firestore().collection)
