import React, {useEffect} from 'react';
import classNames from 'classnames';
import firebase from '../../firebase/firebase.js';

const ScreenPlanet = (props) => {

	const planet = props.planet

	const screenDivClasses = (classNames(
		'screen',
		{flex:planet.infoToggle},
		{opacityOff:planet.infoToggle}
	))

	const screen = (
		<div>
		{planet.id}
		</div>
	)

		const test = []
		useEffect(() => {
		firebase.firestore().collection('planets').onSnapshot((snapshot) =>{
		snapshot.docs.map(doc => test.push(doc))
		} )
		}, [])		
		console.log(test)
	return (

		<div className={screenDivClasses}>
		{screen}
		</div>

	)
}
export default ScreenPlanet;
