import React, {useEffect, useState} from 'react';
import classNames from 'classnames';
import firebase from '../../Firebase/firebase.js';

//----------firebase extractor

function useData() {

		const [data, setData] = useState([])

		useEffect(() => {
				const unsuscribe = firebase.firestore().collection('planets')
				.onSnapshot((snapshot) => {
						const newData = snapshot.docs.map((doc) => ({
						id: doc.id,
						...doc.data()
						}))
				setData(newData)
				})
				return () => unsuscribe()
		}, [])
		return data
}

//----------ScreenPlanet component, itself

const ScreenPlanet = (props) => {

    const data = useData()
	const planet = props.planet

    const dataLocal = (
		data.filter(data => data.category === planet.id).pop()
	)


	const screenDivClasses = (classNames(
		'screen',
		{flex:planet.infoToggle},
		{opacityOff:planet.infoToggle}
	))

	const screen = () => (
		dataLocal === undefined ? 
		'Error loading the page'
		:	
		dataLocal.category +
		dataLocal.title
    )

		console.log(dataLocal)
		console.log(planet)

	return (

		<div className={screenDivClasses}>
		{screen}
		</div>

	)
}
export default ScreenPlanet;
