import React, {createContext, useReducer} from 'react';

export const PlanetsContext = createContext();

export const planetReducer = (planetsTab, action) => {
		switch(action.type){
			case 'switchPlanetToggle':
			    	planetsTab.map(planet => planet.planetToggle=false)
				return [
        			...planetsTab.slice(0, action.index),
        			{
          			...planetsTab[action.index],
          			planetToggle: !planetsTab[action.index].planetToggle
        			},
        			...planetsTab.slice(action.index + 1)
      				];
			case 'switchInfoToggle':
                                return [
                                ...planetsTab.slice(0, action.index),
                                {
                                ...planetsTab[action.index],
                                infoToggle: !planetsTab[action.index].infoToggle 
                                },
                                ...planetsTab.slice(action.index + 1)
                                ];
            		default:
                		return planetsTab;
		}
}

	const PlanetsContextProvider = (props) => {

	const [planetsTab, dispatch] = useReducer(planetReducer, [
		{infoToggleAll:false},
                {id:'news', planetToggle:false, infoToggle:false},
		{id:'about', planetToggle:false, infoToggle:false},
                {id:'art', planetToggle:false, infoToggle:false},
                {id:'socials', planetToggle:false, infoToggle:false},
                {id:'music', planetToggle:false, infoToggle:false},
                {id:'contact', planetToggle:false, infoToggle:false}
        ]);

	return (
		<PlanetsContext.Provider value={{planetsTab, dispatch}}>
		{props.children}
		</PlanetsContext.Provider>
	);
};
export default PlanetsContextProvider
