import React, {useEffect, useState} from 'react';
import classNames from 'classnames';
import firebase from '../../Firebase/firebase.js';
import News from './Screens/News.js';
import Art from './Screens/Art.js';
import Music from './Screens/Music.js';
import About from './Screens/About.js';
import Socials from './Screens/Socials.js';
import Contact from './Screens/Contact.js';

//----------firebase extractor

function useData() {

		const [data, setData] = useState([])

		useEffect(() => {
				const unsuscribe = firebase.firestore().collection('planets')
				.onSnapshot((snapshot) => {
						const newData = snapshot.docs.map((doc) => ({
						id: doc.id,
						...doc.data()
						}))
				setData(newData)
				})
				return () => unsuscribe()
		}, [])
		return data
}

//----------ScreenPlanet component, itself

const ScreenPlanet = (props) => {

    const planet = props.planet

    const dataLocal = useData().filter(data => data.category.toLowerCase() === planet.id).pop()

