import React, {useState} from 'react';
import './assets/css/App.css';
import Intro from './components/Intro/Intro.js';
import Header from './components/Header/Header.js';
import Planets from './components/Planets/Planets.js';
import PlanetsContextProvider from './components/PlanetsContext.js';
import NavBar from './components/NavBar/NavBar.js';

function App() {

//----------Intro logic

	const [introScreen, setIntroScreen] = useState('intro')

	const [introEffect, setIntroEffect] = useState('main')

	function introTimeout() { 

                setIntroScreen('intro introDone')

                setTimeout(() => {setIntroScreen('intro none')
                                setIntroEffect('main mainFadeIn')
                }, 2000)

	};

//-----------Planet logic

	const planetsTab = [
		{pId:'planet0', planetToggle:false},
		{pId:'planet1', planetToggle:false}
	]		

	function switchPlanetToggle(pId) {	

		for(let i=0; i<planetsTab.length; i++){
			if(planetsTab[i].pId === pId) 
			{planetsTab[i].planetToggle = true
			console.log(planetsTab[i].planetToggle)}
			else
			{planetsTab[i].planetToggle = false}
		}
console.log(planetsTab)
	}
//-----------NavBar logic

	const [menuToggle, setMenuToggle] = useState(false)

        const [navBarClass, setNavBarClass] = useState('')

        const [menuClass, setMenuClass] = useState('')

        function navBarToggle() {

                if(menuToggle === false) {

                        setMenuToggle(true);
                        setNavBarClass('navBarOpen');
                        setMenuClass('menuX');
                }

                else {

                        setMenuToggle(false);
                        setNavBarClass('');
                        setMenuClass('');
                }
        };

	return (
		<div className='mainTheme'>

			<Intro introScreen={introScreen} introTimeout={introTimeout}/>

			<PlanetsContextProvider>
				<div className={introEffect}>
					<Header/>

					{planetsTab.map((planet) =>(
					<Planets pId={planet.pId} planetToggle={planet.planetToggle} switchPlanetToggle={switchPlanetToggle}/>
					))}

					<NavBar navBarClass={navBarClass} menuClass={menuClass} navBarToggle={navBarToggle}/>
				</div>
			</PlanetsContextProvider>
		</div>
  );
}

export default App;
