import React, {useContext} from 'react';
import classNames from 'classnames';
import {PlanetsContext} from '../../context/PlanetsContext.js';
import PlanetImg from '../../assets/pics/planet.jpg';
import PlanetInfo from './PlanetInfo.js';
import ResetBtn from './ResetBtn.js';

const Planet = (props) => {

	const {dispatch} = useContext(PlanetsContext);

	const planet = props.planet;

	const index = props.index;

	const planetClasses = (classNames(planet.classes,
		{'planetZoom': planet.planetToggle ? true : false},
		{'planetEnter' : planet.infoToggle ? true : false}
	))

	const planetElement = (
		<div className={planetClasses} onClick={() => dispatch({type: 'switchPlanetToggle', index:index})}>
			<img className='planetImg' src={PlanetImg} alt={planet.id}/>
                        <PlanetInfo planet={planet} index={index}/>
			<ResetBtn planet={planet}/>

		</div>
	)

	return (
		<div>
			{planetElement}		
		</div>
	);
};
export default Planet
