import React, {useContext} from 'react';
import PlanetImg from '../../assets/pics/planet.jpg';
import {PlanetsContext} from '../../context/PlanetsContext.js'; 
import PlanetInfo from './PlanetInfo.js';

const Planets = () => {

	const {planetsTab, dispatch} = useContext(PlanetsContext);

	const planets = (

		planetsTab.map((planet, index) => (
			<div key={planet.number} className={planet.planetToggle ? 'planet planetZoom ' + planet.number : 'planet ' + planet.number} onClick={() => dispatch({type: 'switchPlanetToggle', index:index})}>
				<img className='planetImg' src={PlanetImg} alt={planet.id}/>
				<PlanetInfo planet={planet} index={index}/>
			</div>
			)
		)
	);
	return (
		<div>
			{planets}		
		</div>
	)
};
export default Planets
