import React, {useState} from 'react';
import firebase from '../../Firebase/firebase.js';

const MusicUpdate = (props) => {

		const [dataLocal, setDataLocal] = useState(props.dataLocal.filter(data => data.category === 'Music').pop())

		function handleChange(event) {
				setDataLocal({...dataLocal, [event.target.name]:event.target.value})
		}

		function handleSubmit(event) {
				alert('site updated')
				firebase.firestore().collection('planets').doc('test').set(dataLocal)
				event.preventDefault()
		}
console.log(dataLocal)
		const display = (                                       
				<div>

				<form onSubmit={handleSubmit}>
						<label className='label'>Title :</label>
						<input type='text' name='title' className='textInput' 
						defaultValue={dataLocal.title}
						onChange={handleChange}/>
						<input type='submit' value='Submit'/>

						<label className='label'>New song :</label>
						<input type='text' name='songs' className='textInput' 
						defaultValue='new song to add'
						/>

						<input type='text' name='newLink' className='textInput' 
						defaultValue='new link to add'
						/>

						<ul className='songList'>
						{dataLocal.songs.map(song => (
						<label key={songTitle} className='label'>Song :
						<input type='text' name='song' className='textInput'
						defaultValue={songTitle}
						onChange={handleChange}/>
						<br/>
						</label>
						))}
						</ul>

						<ul className='linkList'>
						{dataLocal.songs.map(song => (
						<label key={songLink} className='label'>Link :
						<input type='text' name='link' className='textInput'
						defaultValue={sonLink}
						onChange={handleChange}/>
						<br/>
						</label>
						))}
						</ul>		

				</form>
				</div>                                                                                 )                

		return (
		<div>
				{display}
		</div>
		)
}
export default MusicUpdate;
