import React from 'react';
import introUniverse from '../../assets/pics/introUniverse.mp4';

const Intro = (props) => {

	const {introScreen, introTimeout} = props

	const title = (
		<div className='title'>
		<div className='titleTara'>TARAYAH</div>WORLD
		</div>
	)

	const galaxy = (

		<div>
		    <video autoPlay muted loop id="myVideo">
				<source src={introUniverse.mp4} type="video/mp4"/>
		    </video>
		</div>

	);

	const enter = (

		<div>
		<button className='introButton' onClick={introTimeout}>
		<div className='introButtonEnter'>ENTER</div>
		prepare to explore

		</button>
		</div>

	);

	return(
	<div className={introScreen}>

		{title}
		{galaxy}
		{enter}

	</div>
	);
};

export default Intro;
