import React, {useState} from 'react';
import firebase from '../../Firebase/firebase.js';

const AboutUpdate = (props) => {

		const [dataLocal, setDataLocal] = useState(props.dataLocal.filter(data => data.category === 'About').pop())

		function handleChange(event) {
				setDataLocal({...dataLocal, [event.target.name]:event.target.value})
		}

		function handleSubmit(event) {
				alert('site updated')
				firebase.firestore().collection('planets').doc('3').set(dataLocal)
				event.preventDefault()
		}

		const display = (                                       
				<div>
						<div className='infoCategory'>
						{dataLocal.category}
						</div>                                          
				<form onSubmit={handleSubmit}>
						<div className='infoTitle'>                                              
						<label>Title :
						<input type='text' name='title' className='textForm' 
						value={dataLocal.title.toString()}
						onChange={handleChange}/>
						</label>
						</div>
						<div className='aboutContent'>
						<label>Content :
						<textarea name='content' className='messageForm' 
						value={dataLocal.content.toString()} 
						onChange={handleChange}/>
						</label>
						<input type='submit' value='Submit'/>
						</div>
				</form>
				</div>                                                                                 )                

		return (
		<div>
				{display}
		</div>
		)
}
export default AboutUpdate;
