import React from 'react';
import firebase from '../../Firebase/firebase.js';

const AboutUpdate = (props) => {

		const dataLocal = props.dataLocal.filter(data => data.category === 'About').pop()

		function handleContentChange(event) {
				firebase.firestore().collection('planets').doc('3').set({collection:event.target.value })
		}

		function handleTitleChange(event) {
				firebase.firestore().collection('planets').doc('3').set({title:event.target.value })
		}

		function handleSubmit(event) {
				alert('site updated')
				event.preventDefault()
		}

		const display = (                                       
				<div>
						<div className='infoCategory'>
						{dataLocal.category}
						</div>                                          
				<form onSubmit={handleSubmit}>
						<div className='infoTitle'>                                              
						<label>Title :</label>
						<input type='text' className='textForm' 
						value={dataLocal.title}
