						<label>Content :</label>
						<textarea className='messageForm' 
						value={dataLocal.content.replace(/_n/g,"\n").toString()} 
						onChange={handleContentChange(e)}/>
						<button className='aboutSubmit' type='submit'>Submit</button>
						</div>
				</form>
				</div>                                                                                 )                

		return (
		<div>
				{display}
		</div>
		)
}
