import React, {useContext} from 'react';
import classNames from 'classnames';
import {PlanetsContext} from '../../context/PlanetsContext.js';
import newsImg from '../../assets/pics/NewsImg.jpg';
import aboutImg from '../../assets/pics/AboutImg.jpg';
import socialsImg from '../../assets/pics/SocialsImg.jpg';
import contactImg from '../../assets/pics/ContactImg.jpg';
import musicImg from '../../assets/pics/MusicImg.jpg';
import artImg from '../../assets/pics/ArtImg.png';

const Planet = (props) => {

	const {dispatch} = useContext(PlanetsContext);

	const planet = props.planet;

	const index = props.index;

	const planetClasses = (classNames(
		'planet', 
		planet.classes,
		{'planetZoom': planet.planetToggle},
		{'planetEnter' : planet.infoToggle}
	))

    const planetTextClasses = (classNames(
			'planetText',
			{'flex': planet.planetToggle && !planet.infoToggle}
    ))

    const planetImg = () => {
		if(planet.id === 'news'){return {newsImg}}
		else if(planet.id === 'about'){return {aboutImg}}
		else if(planet.id === 'socials'){return {socialsImg}}
		else if(planet.id === 'contact'){return {contactImg}}
		else if(planet.id === 'music'){return {musicImg}}
		else if(planet.id === 'art'){return {artImg}}
	}

	const planetElement = (
		<div className={planetClasses} onClick={() => dispatch({type: 'switchInfoToggle', index:index})}
				onMouseEnter={() => dispatch({type: 'switchPlanetToggle', index:index})}
				onMouseLeave={() => dispatch({type: 'switchPlanetToggle', index:index})}>
			<img className='planetImg' src={planetImg} alt={planet.id}/>
			<div className={planetTextClasses}>{planet.id}</div>
		</div>
	)

	return (
		<div>
			{planetElement}		
		</div>
	);
};
export default Planet
