import React, {useContext} from 'react';
import classNames from 'classnames';
import {PlanetsContext} from '../../context/PlanetsContext.js';

const Planet = (props) => {

	const {planetsTab, dispatch} = useContext(PlanetsContext);

	const planet = props.planet;

        const index = props.index;

	const planetClasses = (classNames('planet', 'planet.number',
		{planetZoom: planet.planetToggle ? true : false}

	))

	const planetElement = (
		<div className={planetClasses} onClick={() => dispatch({type: 'switchPlanetToggle', index:index})}/>

	)

	return (
		<div>
			{planetElement}		
		</div>
	);
};
export default Planet
