import React from 'react';
import PlanetImg from '../../assets/pics/planet.jpg'
import classNames from 'classnames';

const Planets = (props) => {

	const {pId, planetToggle, switchPlanetToggle} = props

	const [planetToggleS, setPlanetToggleS] = React.useState(planetToggle)

	function handleClick() {
		switchPlanetToggle(pId);
		setPlanetToggleS(!planetToggleS);
	}

console.log(planetToggle)
console.log(planetToggleS)
	const infoClasses = classNames('planetInfo',{
	'flex': planetToggleS
	})

	const planetClasses = classNames('planet', pId)

	const planet = (

		<div className={planetClasses} onClick={handleClick}>

			<img src={PlanetImg} alt='planet' className='planetPic'/>
			<div className={infoClasses}>
			<button>Enter</button>
			</div>
		</div>
	)

	return (
	<div>
		{planet}
	</div>
	);
};
export default Planets
