import React, {useContext} from 'react';
import classNames from 'classnames';
import {PlanetsContext} from '../../context/PlanetsContext.js';

const PlanetInfo = (props) => {

	const planet = props.planet;

	const index = props.index;

	const {planetsTab, dispatch} = useContext(PlanetsContext);

	const infoClasses = (classNames(
		'planetInfo', 
		{'flex':planet.planetToggle && !planet.infoToggle? true : false}
	))

	const planetInfo = (
		<div className={infoClasses}>
			{'here are the info of planet ' +index}
			<div className='infoButton' onClick={() => dispatch({type: 'switchInfoToggle', index:index})}/>
		</div>
	)
console.log(planetsTab)
	return (
		<div>
			{planetInfo}
		</div>
	);
};
export default PlanetInfo;
